-- Database Creation and Backup --

create database RPG
default character set utf8
default collate utf8_general_ci;

use RPG;

create table racas(
	id_raca int primary key not null auto_increment,
    nome varchar(50) not null,
	up_valor_habilidade text,
	proeficiencias text,
    skills text,
    idiomas text,
    classes_fav text
    ) default charset = utf8;
    

