create table classes(
	id_classe int primary key not null auto_increment,
    nome varchar(50) not null,
	dado_de_vida varchar(20),
    vida int,
	habilidade text,
	tem_magia boolean,
    espaco_magia int
    ) default charset = utf8;
    
    
insert into classes
(nome, dado_de_vida, vida, habilidade, tem_magia, espaco_magia)
values
