('Paladino', '1d10', 10, 'Canalizar Divindade: Pode canalizar poder divino para efeitos específicos.', False, null),
('Patrulheiro', '1d10', 10, 'Rastreio: Possui habilidades de rastreamento e sobrevivência.', False, null),
